import * as types from "../../actionTypes";
import MunicipioApi from "../../../api/MunicipioApi";

let actionsFunction = {

  //CRUD METHODS

  // Create municipio
  createMunicipio: function(municipio) {
    return function(dispatch) {
      return MunicipioApi
        .createMunicipio(municipio)
        .then(municipio => {
          dispatch(actionsFunction.createMunicipioSuccess(municipio));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createMunicipioSuccess: function(municipio) {
    return { type: types.CREATE_MUNICIPIO_SUCCESS, payload: municipio };
  },


  // Delete municipio
  deleteMunicipio: function(id) {
    return function(dispatch) {
      return MunicipioApi
        .deleteMunicipio(id)
        .then(municipio => {
          dispatch(actionsFunction.deleteMunicipioSuccess(municipio));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  deleteMunicipioSuccess: function(municipio) {
    return { type: types.DELETE_MUNICIPIO_SUCCESS, payload: municipio };
  },


  // Find by Departamento
  findByDepartamento: function(key) {
    return function(dispatch) {
      return MunicipioApi
        .findByDepartamento(key)
        .then(item => {
          dispatch(actionsFunction.findByDepartamentoSuccess(item));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  findByDepartamentoSuccess: function(item) {
    return { type: types.FINDBYDEPARTAMENTO_MUNICIPIO_SUCCESS, payload: item };
  },


  // Get municipio
  loadMunicipio: function(id) {
    return function(dispatch) {
      return MunicipioApi
        .getOneMunicipio(id)
        .then(municipio => {
          dispatch(actionsFunction.loadMunicipioSuccess(municipio));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadMunicipioSuccess: function(municipio) {
    return { type: types.GET_MUNICIPIO_SUCCESS, payload: municipio };
  },

  // Load  list
  loadMunicipioList: function() {
    return function(dispatch) {
      return MunicipioApi
        .getMunicipioList()
        .then(list => {
          dispatch(actionsFunction.loadMunicipioListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadMunicipioListSuccess: function(list) {
    return { type: types.LIST_MUNICIPIO_SUCCESS, payload: list };
  },

	
  // Save municipio
  saveMunicipio: function(municipio) {
    return function(dispatch) {
      return MunicipioApi
        .saveMunicipio(municipio)
        .then(municipio => {
          dispatch(actionsFunction.saveMunicipioSuccess(municipio));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  saveMunicipioSuccess: function(municipio) {
    return { type: types.UPDATE_MUNICIPIO_SUCCESS, payload: municipio };
  },


};

export default actionsFunction;
